// frontend/components/ImageUpload.js
import React, { useState } from 'react';

const ImageUpload = () => {
  const [image, setImage] = useState(null);
  const [isUploading, setIsUploading] = useState(false);
  const [colors, setColors] = useState([]);

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setImage(URL.createObjectURL(file));  // Display the image
    }
  };

  const handleUpload = async () => {
    if (!image) return;
    setIsUploading(true);

    const formData = new FormData();
    formData.append('image', document.getElementById('image-input').files[0]);

    try {
      const response = await fetch('http://127.0.0.1:5000/upload', {
        method: 'POST',
        body: formData,
      });
      const result = await response.json();
      if (response.ok) {
        setColors(result.top_colors);
      } else {
        alert('Error: ' + result.error);
      }
    } catch (error) {
      alert('Error uploading image: ' + error.message);
    } finally {
      setIsUploading(false);
    }
  };

  return (
    <div>
      <div>
        <label className="upload-btn" htmlFor="image-input">
          Select Image
        </label>
        <input
          type="file"
          id="image-input"
          accept="image/*"
          onChange={handleImageChange}
          style={{ display: 'none' }}
        />
      </div>
      <button onClick={handleUpload} disabled={isUploading}>
        {isUploading ? 'Uploading...' : 'Upload Image'}
      </button>

      {image && <img src={image} alt="Selected" style={{ width: '100%', maxWidth: '300px', marginTop: '10px' }} />}

      {colors.length > 0 && (
        <div className="color-palette">
          <h3>Top 10 Colors:</h3>
          <div className="palette">
            {colors.map((color, index) => (
              <div
                key={index}
                style={{
                  backgroundColor: `rgb(${color[0]}, ${color[1]}, ${color[2]})`,
                  width: '40px',
                  height: '40px',
                  margin: '5px',
                }}
              ></div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default ImageUpload;
